SELECT s.id,s.staff_name,s.sex,s.entry_time,a.depart_name,a.post_name FROM t_staff s,
(SELECT p.id id,depart_name,post_name FROM t_post p,t_department d WHERE d.id=p.depart_id) a
WHERE s.post_id=a.id limit #{start},#{count}

SELECT s.id,s.staff_name,s.sex,s.entry_time,a.depart_name,a.post_name FROM t_staff s,
(SELECT p.id pid,d.id did,depart_name,post_name FROM t_post p,t_department d WHERE d.id=p.depart_id) a
WHERE s.post_id=a.pid

	
		
			and	a.post_name=#{staff.post.post_name}
		
		
			
				and a.depart_name=#{staff.post.department.depart_name}
			
		
	
	
		
		and staff_name like CONCAT('%',#{staff.staff_name},'%')
	

 limit #{start},#{count}

SELECT count(*) FROM t_staff s,
(SELECT p.id pid,d.id did,depart_name,post_name FROM t_post p,t_department d WHERE d.id=p.depart_id) a
WHERE s.post_id=a.pid

	
		
			and	a.post_name=#{staff.post.post_name}
		
		
			
				and a.depart_name=#{staff.post.department.depart_name}
			
		
	
	
		
		and staff_name like CONCAT('%',#{staff.staff_name},'%')

SELECT s.id,s.Loginname,s.password,s.staff_name,s.sex,s.entry_time,a.depart_name,a.post_name FROM t_staff s,
(SELECT p.id id,depart_name,post_name FROM t_post p,t_department d WHERE d.id=p.depart_id) a
WHERE s.post_id=a.id and s.id=#{id}
